// 1. Import `extendTheme`
import { extendTheme } from "@chakra-ui/react";

// 2. Call `extendTheme` and pass your custom values
const theme = extendTheme({
  colors: {
    brand: {
      100: "#f7fafc",
      // ...
      900: "#1a202c",
    },
  },
  l_shadow: {
    pink: "0 0 0 3px rgba(237, 100, 166, 0.6)",
  },
});

export default theme;
